	/**
	 * Draws a 2D array to the command line.
	 * This is used for testing the output of makeSwoosh().
	 */
	
	public static void drawGrid(String[][] grid) throws IOException{
		// draw the complete logo
		for (int i = 0; i < grid.length; i++) {
			for (int j = 0; j < grid[i].length; j++) {
				System.out.print(grid[i][j] + " ");
			}
			System.out.println();
		}
	}
	/**
	 * Makes a JavaScript file named data.js, containing data necessary for front-end rendering.
	 */ 
	public static void writeToJS(String[][] grid) throws IOException {
		final String FILENAME = "output.js";
		BufferedWriter bw = null;
		FileWriter fw = null;
		try {
			fw = new FileWriter(FILENAME);
			bw = new BufferedWriter(fw);
			//bw.write("var grid1 = " + grid);
			bw.write("var grid = [");
			for(int i = 0; i<grid.length;i++) {
				bw.write("[");
				for(int j = 0; j<grid[i].length;j++) {
					String s = grid[i][j];
					bw.write("\'" + grid[i][j] + "\'" + ", ");
				}
				bw.write("],");
				bw.write(" \n");
			}
			bw.write("];");
		} finally {
			if(bw != null)
			{
				bw.close();
			}
			if( fw != null)
				fw.close();
		}
	} 

	public static void main(String[] args)  throws IOException{
		System.out.println("aeeerterte");
		System.out.println(args[0]);
		int width = Integer.parseInt(args[0]);
		System.out.println("The value of width is" + width);
		String[][] grid = makeSwoosh(width);


		//uncomment this to draw grid to command line
		drawGrid(grid);

		writeToJS(grid); 
	}
